definition.customCSS:
.[$FRAGMENT_CLASS$] {
border-bottom: 1px solid var(--gray-300);
position: sticky;
  top: calc(var(--control-menu-container-height, 0px) + 7.75rem);
  z-index: 899;
}

.commerce-main header {
top: calc(var(--control-menu-container-height) - var(--commerce-header-user-height, 0));
}

.commerce-header-user {
position: sticky;
top: calc(-1 * var(--commerce-header-user-height));
}

.[$FRAGMENT_CLASS$] nav.navbar.navbar-collapse-relative.navbar-expand-md.navbar-underline.navigation-bar.navigation-bar-light::before {
    background-color: var(--white);
    content: '';
    height: -webkit-fill-available;
    left: 50%;
    margin-left: -50vw;
    position: absolute;
    width: 100vw;
}

.[$FRAGMENT_CLASS$] {
border-left: 1px solid var(--gray-300);
border-right: 1px solid var(--gray-300)
}

.[$FRAGMENT_CLASS$] .order-summary-label {
display: inline-block;
text-align: right;
min-width: 40%;
}
.[$FRAGMENT_CLASS$] .order-summary-value {
display: block;
float: right;
}

